import React, { useState, useEffect } from 'react'
import { BrowserRouter, Switch, Route } from 'react-router-dom';
import Axios from 'axios';
import Home from './components/Pages/Home';
import Login from './components/Auth/Login';
import Register from './components/Auth/Register';
import Header from './components/Layout/Header';
import UserContext from './context/UserContext';

export default function App() {
  const [userData, setUserData] = useState({
    token: undefined,
    user: undefined,
  });

  useEffect(() => {
    //Because you can't set useEffect to be asynchronous we have to define an asynchronous function within it
    const checkLoggedIn = async () => {
       //Right now we are just using local storage to retrieve the tokens
       const token = localStorage.getItem("auth-token");
       const tokenResponse = await Axios.post(
         "http://localhost:5000/users/tokenIsValid", null,
         {
           headers: { "x-auth-token": token }
         }
       );
       console.log(tokenResponse.data);
    };

    checkLoggedIn();
  }, []);
  //Second parameter is states and runs it when that state is changed, keeping the array empty means that ti'll run only once when the app starts

  return (
    <>
      <BrowserRouter>
        <UserContext.Provider value={{ userData, setUserData }}>
          <Header />
          <Switch>
            <Route exact path="/" component={Home} />
            <Route path="/login" component={Login} />
            <Route path="/register" component={Register} />
          </Switch>
        </UserContext.Provider>
      </BrowserRouter>
    </>
  );
}
